/*Table structure for table `employee` */

DROP TABLE IF EXISTS `employee`;

CREATE TABLE `employee` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `name` varchar(30) DEFAULT NULL COMMENT '员工姓名',
  `password` varchar(30) DEFAULT '123456' COMMENT '登录密码',
  `image` varchar(150) DEFAULT NULL COMMENT '头像',
  `sex` varchar(4) DEFAULT NULL COMMENT '性别',
  `phone` varchar(20) DEFAULT NULL COMMENT '手机号',
  `ic_card` varchar(25) DEFAULT NULL COMMENT '身份证号码',
  `address` varchar(50) DEFAULT NULL COMMENT '员工住址',
  `email` varchar(30) DEFAULT NULL COMMENT '邮箱',
  `remark` varchar(100) DEFAULT NULL COMMENT '备注',
  `last_login_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '最后一次登录日期',
  `state` int(11) DEFAULT '1' COMMENT '员工状态, 0冻结, 1正常, -1删除',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=utf8 COMMENT='员工表';

